package com.example.kids_love_cloneapp_test01.click_album;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.view.View;
import android.view.inputmethod.InputMethodManager;
import android.widget.EditText;
import android.widget.ScrollView;
import android.widget.TextView;
import android.widget.Toast;

import com.example.kids_love_cloneapp_test01.Adapters.Click_AlbumItem_Adapter;
import com.example.kids_love_cloneapp_test01.Items.Album_clickItems;
import com.example.kids_love_cloneapp_test01.R;

import java.util.ArrayList;
import java.util.Date;

public class Click_Album extends AppCompatActivity {
//    클릭해서 들어온 엑티비티임
    RecyclerView recyclerView;
    ArrayList<Album_clickItems> items=new ArrayList<>();
    Click_AlbumItem_Adapter adapter;

    TextView title_parent, title_from, title_date, msg;
    EditText recomend;
    ScrollView scrollView;
    String getRecomend;
    InputMethodManager imm;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_click__album);
        title_parent=findViewById(R.id.album_item_parent_text);
        title_from=findViewById(R.id.album_item_from_text);
        title_date=findViewById(R.id.album_item_date_text);
        msg=findViewById(R.id.getAlbum_item_msgText);
        recyclerView=findViewById(R.id.getAlbum_item_images);
        recomend=findViewById(R.id.et_album_item_recomend);
        scrollView=findViewById(R.id.alvum_item_scrollview);
        loadData();
        adapter=new Click_AlbumItem_Adapter(this, items);
        recyclerView.setAdapter(adapter);
        imm=(InputMethodManager) getSystemService(INPUT_METHOD_SERVICE);
        

    }


    public void loadData(){
//        서버에서 받아온 제목, 내용, 앨범 업로드시간, 메시지들을 적용
        title_parent.setText("나일등의 부모님께");
        title_from.setText("From. 등하원테스트");
        title_date.setText("2020년 12월 8일 오후 2:26");
        msg.setText("test용 메시지 작성 및 송출");

        for (int i=0;i<8;i++){
            items.add(new Album_clickItems("data:image/jpeg;base64,"));
        }
    }

    public void click_back_album(View view) {finish();}

    public void click_share_album_item(View view) {
//        공유하기 버튼
    }

    public void click_recomend_send_album(View view) {
//        댓글등록 버튼
        getRecomend=recomend.getText().toString();// String저장함
        recomend.setText("");

        imm.hideSoftInputFromWindow(recomend.getWindowToken(),0);


        Toast.makeText(this, getRecomend, Toast.LENGTH_SHORT).show();
    }
}